searchUrl:
@js:
//æ³¨æ„ï¼šä¸€èˆ¬ç½‘ç«™é»˜è®¤é¦–é¡µ(å³baseUrl)æœ‰æœç´¢æ¡†ï¼Œå¦åˆ™è¯·å¡«å…¥æœç´¢é¡µé¢åœ°å€
let url = baseUrl;
let html = java.ajax(url);
let doc = org.jsoup.Jsoup.parse(html);
let form = doc.select('form[action]:not([name~=login]):not([class~=login]):not([id~=login])')[0];
let method = form.attr('method');
let action = form.attr('action');
let charset = doc.select('meta[charset]').attr('charset');
if (charset == ''){
    charset = String(doc.select('meta[content~=charset=]').attr('content')).replace(/^.*charset=([a-z0-9\-]+).*$/i, 
    '$1');
}
url = baseUrl.replace(/([^\/])$/i, '$1/') + String(action).replace(/^\//, '');
let body = '';
let option = {};
let sk = form.select('input[name][type="text"],input[name][class="text"]').attr('name');
let es = form.select('input[name]:not([type="submit"])');
for (let it in es){
    let e = es[it];
    let k = e.attr('name');
    let v = e.attr('value');
    if (k == sk) {
        body += sk + '={{key}}&';
    }
    else {
        body += k + '=' + v + '&';
    }
}
es = form.select('select[name]');
for (it in es){
    let e = es[it];
    let k = e.attr('name');
    let v = e.select('option')[0].attr('value');
    body += k + '=' + v + '&';
}
body = String(body).replace(/&$/, '');
if (method.match(/post/i)){
    option = 
    {
        "charset" : String(charset), 
        "method" : String(method), 
        "body" : String(body)
    };
    url += ',' + JSON.stringify(option);
}
else {
    //get
    option = 
    {
        "charset" : String(charset)
    };
    url += '?' + body + ',' + JSON.stringify(option);
}
java.log(url);

