var i=java.getString(path1);
var d=java.getString(path2);
i=String(i);d=String(d);
var url=i.replace(/\d+.html/,"");
var start=i.match(/(\d+).html/)[1];
var ends=d.match(/(\d+).html/)[1];
var list=[{text:"第1章",href:i}];
if(ends-start<10000){
	for(var c=1;start<=ends;c++,start++){
	list.push({text:`第${c}章`,href:url+start+".html"})
	}
	list;
}else{
	java.toast("目录异常，获取失败！")
	list=[{text:"正文",href:i}]
	list;
	}

