ruleContent.nextContentUrl:
@js:
(()=>{try{
var html = result
var p = /chapterPages[\s\S]*?<\/center/
var ss = html.match(p)[0]
var as = ss.match(/<a.*?>/g)
var len = as.length
var i = 0
for(i=0;i<len;i++){
	if(as[i].indexOf("curr")>0){
		break
		}
	}
if (i!=len-1){
	return as[i+1].match(/href="(.*?)"/)[1]
	}
else{
	return html.match(/href="(.*?)".*?class="next"/)[1]
	}}
	catch(e){return e}
	})()

