var url=source.getKey();
var html=java.ajax(url);
sa=org.jsoup.Jsoup.parse(html).select('form[name=search]').attr('action');
sn=org.jsoup.Jsoup.parse(html).select('input[class=search]').attr('name');
var so=`${sa}?${sn}=${key}`;
url+so

ruleContent.content:
@js:
url="https:"+result.match(/initTxt\("([^"]+)","[^"]+"\)/)[1];
r=String(java.ajax(url).match(/_txt_call\(([\s\S]+\})\)/)[1]);
var r = eval('(' + r + ')');
				if(r.content!=null){				
      var e = r.replace;
				for (var n in e) {
					var i = new RegExp(e[n], "ig");
					r.content = r.content.replace(i, n)
				}
				result=r.content
					}else{result="章节加载失败，或者内容正在手打中，请稍后访问~"}

