bookSourceComment:
//登录设置分卷，默认启用
//类型：文本 在线阅读
//切换：文件 可以下载

loginUi:
[
    {
    	   name: "     设置分卷     ",
        type: "button",
        action: "java.toast('点击下面按钮即可设置，设置后需要刷新目录。')"
    },
    {
    	   name: "     登录下载     ",
    	   type: "button",
    	   action: " login('http://www.cuoheng.com/login.php') "
    	},
    {
        name: "                       当前状态                       ",
        type: "button",
        action: "get(v)"
    },
    {
        name: "     启用分卷     ",
        type: "button",
        action: "set(1)"
    },
    {
        name: "     禁用分卷     ",
        type: "button",
        action: "set(0)"
    }
]

loginUrl:
var sz=source.getVariable()
if(sz==null||sz==""){
	sz=1;source.setVariable(1);
	java.toast("未设置分卷，默认启用分卷。")
	}else{sz=sz};
	pc=',{"headers": {"User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/101.0.4951.41 Safari/537.36"} }';
var v=sz==1?true:false;

function login(url){
	url=url+pc;
  java.startBrowser(url,"登录");
  return
	}

function set(num){
	source.setVariable(num);
	show=num==1||0?"已启用分卷！":"已禁用分卷！";
	java.toast("设置成功！"+show);
	return
	}

function get(v){
	show=v?"分卷启用！":"分卷禁用！";
	java.toast("当前状态："+show);
	return
	}

