eval(String(source.loginUrl));
var url=baseUrl+pc;
var volume=v?true:false;
var html=java.ajax(url);
result=java.getString("text.TXT全文@href",html);
if(volume==false){
	url=source.getKey()+result;	
	url+=pc;
	java.startBrowser(url,"下载");
		}else{
			java.longToast("如需下载，登录关闭分卷即可");
			}
result;

ruleToc.chapterList:
@js:
var the="dl dd a";
var juan="dl dt";
var th=/第[〇零一二三四五六七八九十百]+卷.*|第0+章|.*内容简介/g;
eval(String(source.loginUrl));
v==true||false?is(the,juan,th):no(the,th);

function is(path,volume,regex){
