Rank_type = [["人气","1"],["好评","2"],["热销","3"]];

	var v=source.getVariable();
	if(v==null||v==""){
		rank = Rank_type[0],Name=rank[0],rank_type=rank[1];
		java.toast('\n❗未设置❗\n默认启用		 【'+Name+'分类】 接口');
		source.setVariable("0");
	}
 else{
var v = v.match(/\d+$/)[0],
 rank = Rank_type[v],Name=rank[0],rank_type=rank[1];
 java.toast('\n❗当前设置：【'+Name+'分类】 接口');
 }

function get(n){	  
	rank = Rank_type[n];
	java.toast("当前接口："+rank[0]);
}	

function set(i){
	rank = Rank_type[i];
	java.toast("设置成功！"+rank[0]);
	return source.setVariable(i)
	}

