x(/(^|[\r\n])(<p>)?[^><]*(\>|&gt;)/gi,"$2");
x(/<p>\s*o([\r\n]|$)/g,'');


//开头是否包含段落截断标识
x(/^(?!(\<p\>))/i,"$1hereispagebreak");

//执行替换，不能删
