ruleToc.chapterList:
@js:
//获取开始章节的id
start=java.getString('@@class.chapterList@tag.li.0@a@href').match(/(\d+)\.html/)[1];

//获取总章节数
page=result.match(/共(\d+)章节/)[1];
list=[];

//利用总章节，拼接目录
for(i=0;i<page;i++){
id=Number(start)+i;
list.push({text:String('分卷阅读'+Number(i+1)),href:baseUrl.replace(/\.html/,'/'+id+".html")})
}
list
