ruleContent.nextContentUrl:
@js:
var next=/next1.png/.test(src);
var code=src.match(/eval(\(function.*)/);
url=next?eval(code[1]):"";
url?eval(url.replace(/var/,"")):"";

searchUrl:
@js:
var url=source.key;
var html=java.ajax(url);
token=org.jsoup.Jsoup.parse(html).select('input[name=_token]').attr('value');
var so="/search,";
var body=`_token=${token}&keyword=${key}`;
var post={
  "body": String(body),
  "method": "POST"
}
url+so+JSON.stringify(post)
