ruleSearchList:
@js:
function check(a){return (a)? a[1]:'';}
importClass(org.jsoup.Jsoup);
//期望获取的书籍个数
var n=2;
//从搜索引擎的结果获取书籍网址
var reg=new RegExp(/vr-tit[\s\S]+?url=(.*?)&amp/g);
var url;
var url_pre;
var list=[];

while((url=reg.exec(result))){
url=decodeURIComponent(check(url)).replace(/m/,'www');
//避免重复获取相同网址,处理书籍计数
while((url==url_pre)){
url=reg.exec(result);
url=decodeURIComponent(check(url)).replace(/m/,'www');
}
url_pre=url;
n=n-1;

//打开链接获取书籍信息
var tmp=new String(java.ajax(url+"@header:{'User-Agent':'Chrome'}"));
var tt=Jsoup.parse(tmp);
var name=tt.select("[property=og:title]").attr("content")

//var name=check((tmp.match(/og:title" content="(.*?)"/)));

var author=check((tmp.match(/og:novel:author" content="(.*?)"/)));

var type=check((tmp.match(/og:novel:category" content="(.*?)"/)));

var last=check((tmp.match(/最新章节预览[\s\S]+?href.*?>(.*?)</)));

var intro=check(tmp.match(/intro">([^<]+)/)).trim();

var img=check((tmp.match(/og:image" content="(.*?)"/)));


//保存数据
list.push({name:name,author:author,type:type,img:img,last:last,intro:intro,url:url});
//判断书籍计数
if(n==0) break;
}
list;
