ruleContent.payAction:
// 解密
let key = java.base64DecodeToByteArray("lXsAUe1NZEw6tAxM9w1Tpq2tU4la2+PwM8evj2C+yB0=")
let iv = java.base64DecodeToByteArray("AAAAAAAAAAAAAAAAAAAAAA==")
let aes = Packages.cn.hutool.crypto.symmetric.AES("CBC","PKCS5Padding", key, iv)
// 参数
let headerMap = source.getLoginHeaderMap()
let app_version = headerMap['app_version']
let account = headerMap['account']
let login_token = headerMap['login_token']
let device_token = headerMap['device_token']
let shelf_id = headerMap['shelf_id']
let chapter_id = chapter.url.match(/chapter_id=(\d+)/)[1]
let params = `app_version=${app_version}&device_token=${device_token}&login_token=${login_token}&account=${account}`
// 开始购买
let payUrl = `https://sk.hbooker.com/chapter/buy?${params}&shelf_id=${shelf_id}&chapter_id=${chapter_id}`
let payRes = JSON.parse(aes.decryptStr(java.ajax(payUrl)))
if(payRes.tip){
 java.toast(payRes.tip)
 throw new Error(payRes.tip)	
}
// 更新书币
let propUrl = `https://sk.hbooker.com/reader/get_prop_info?${params}`
let propRes = JSON.parse(aes.decryptStr(java.ajax(propUrl)))
if(propRes.tip){
 java.toast(propRes.tip)
 throw new Error(propRes.tip)	
}
let rest_hlb = propRes.data.prop_info.rest_hlb
java.toast(`购买成功,剩余书币${rest_hlb},请手动刷新正文`)
source.putLoginHeader(Object.assign(headerMap,{'rest_hlb': String(rest_hlb)}))
