ruleToc.chapterList:
article||.booklist@span||.book_list@a
@js:
list=[]
$=result
function $$($){
		return list.push(JSON.stringify($))
	}


//云中歌&&乡村小说
if(baseUrl.match(/yunzhongge|xiangcun2018/)){for(i in $){
if(/^\s*$/.test(String(a=$[i].select('a'))))
		$$({text:$[i].text(),volume:true});
else
		$$({text:a.text(),href:a.attr('href')});
	}}


//三优无错
else if(baseUrl.match(/333uu|wcxs5/)){
	for(i in $){
		a=$[i].select('.a-link')
		text=a.text()
		href=a.attr('href')
		a=$[i].select('.float-right span').text()
		b=$[i].select('p').text()
		info='⏱️ '+a+'　📑 '+b
		$$({text:text,href:href,info:info})
		}
	}
list
