ruleSearch.bookList:
@js:
url="https://www.123ds.org/Search/,";
post={
"charset": "gbk",
"method": "POST",
"body": "st=0&q="+java.get("key"),
"webView":"true"
}
post2={"charset": "gbk",
"method": "POST",
"body": "st=0&q="+java.get("key")}
url1=url+JSON.stringify(post);
url2=url+JSON.stringify(post2);
result=java.ajax(url1);
result=java.ajax(url2);
list=result.match(/小说：<a[\s\S]+?<hr/g);
json=[];
for(i=0;i<list.length;i++){
doc=org.jsoup.Jsoup.parse(list[i]);
title=doc.select('.Title').text();
href=doc.select('.Title').attr('href');
tags=doc.select('font').toArray();
author=tags[1].text();tag=tags[2].text()+','+tags[3].text();
intro=doc.select('.DivSmallInstro').text();
json.push({href:href,title:title,tag:tag,author:author,intro:intro})
}
json
