let bdField='0,allvisit,monthvisit,weekvisit,dayvisit,allvote,monthvote,weekvote,dayvote,goodnum,size,postdate'.split(',')
let 全部榜单=[`{"title":"↓状态：全部↓","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`]
let 连载榜单=[`{"title":"↓连载↓","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`]
let 完本榜单=[`{"title":"↓完本↓","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`]
for(let i=0;i<类别.length;i++){
  全部榜单.push(`{"title":"`+类别[i]+`","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`)
  for(let j=0;j<bdField.length;j++){
    全部榜单.push(`{"title":"`+榜单[j]+`","url":"/shuku/`+bdField[j]+'_'+i+`_0_{{page}}.html","style":{"layout_flexGrow":1,"layout_flexBasisPercent":0.2}}`)
  }
}
for(let i=0;i<类别.length;i++){
  连载榜单.push(`{"title":"`+类别[i]+`","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`)
  for(let j=0;j<bdField.length;j++){
    连载榜单.push(`{"title":"`+榜单[j]+`","url":"/shuku/`+bdField[j]+'_'+i+`_1_{{page}}.html","style":{"layout_flexGrow":1,"layout_flexBasisPercent":0.2}}`)
  }
}
for(let i=0;i<类别.length;i++){
  完本榜单.push(`{"title":"`+类别[i]+`","url":"","style":{"layout_flexGrow":1,"layout_flexBasisPercent":1}}`)
  for(let j=0;j<bdField.length;j++){
    完本榜单.push(`{"title":"`+榜单[j]+`","url":"/shuku/`+bdField[j]+'_'+i+`_2_{{page}}.html","style":{"layout_flexGrow":1,"layout_flexBasisPercent":0.2}}`)
  }
}
  let list=全部榜单.concat(完本榜单,连载榜单)
   '['+ list.join(',') +']'
